import React, { useState } from 'react'
import Template from '../components/Template'

const ReportSumSalePerMonth = () => {
    const [years, setYears] = useState(() => {
        let arr = [];
        let d = new Date();
        let currentYear = d.getFullYear()
        let lastYear = (currentYear - 5);

        for (let index = lastYear; index < currentYear; index++) arr.push(index)
        return arr;
    })

    const [currentYear, setCurrentYear] = useState(() => {
        return new Date().getFullYear()
    })

    const [report,setReport] = useState


    return (
        <>
            <Template>
                <div className='card'>
                    <div className="card-header">
                        <div className='card-title'>รายงานสรุปยอดขายรายเดือน</div>
                    </div>
                    <div className='card-body'>
                        <div className='input-group'>
                            <span className='input-group-text'>ปี</span>
                            <select value={currentYear} className='form-control'>
                                {
                                    years.map((item) => (
                                        <option value={item}>{item}</option>
                                    ))
                                }
                            </select>
                            <button className='btn btn-primary'>แสดงผล</button>
                        </div>

                        <table className='table table-bordered table-striped mt-3'>
                            <thead>
                                <tr>
                                    <th>เดือน</th>
                                    <th>ยอดขาย</th>
                                    <th></th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td></td>
                                    <td></td>
                                    <td></td>

                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </Template>
        </>
    )
}

export default ReportSumSalePerMonth
