import React from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Col, Button, Nav } from 'react-bootstrap';

const Template = (props) => {
  return (
    <Container fluid className="vh-100 bg-light">
      <Row className="h-100">
        {/* Sidebar */}
        <Col
          xs={12}
          md={3}
          xl={2}
          className="bg-dark text-white d-flex flex-column justify-content-between p-3 shadow"
        >
          <div>
            <h4 className="text-center mb-4 fw-bold border-bottom pb-2">
              เมนูหลัก
            </h4>
            <Nav className="flex-column">
              <Nav.Link as={Link} to="/" className="text-white mb-2 rounded btn btn-outline-light">
                ผู้ใช้งานทั้งหมด
              </Nav.Link>
              <Nav.Link as={Link} to="/members" className="text-white mb-2 rounded btn btn-outline-light">
                ผู้ใช้งานที่สมัครสมาชิก
              </Nav.Link>
            </Nav>
          </div>

          <div className="text-center mt-auto border-top pt-3">
            <small>© 2025 MyApp</small>
          </div>
        </Col>

        {/* Main Content */}
        <Col xs={12} md={9} xl={10} className="p-4">
          <div className="bg-white rounded shadow-sm p-4 h-100 overflow-auto">
            {props.children}
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Template;
