import Template from "../components/Template"

import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js'
import { Bar } from 'react-chartjs-2'
import axios from "axios"
import { useEffect, useState } from "react"
import ResponseError from "../utils/ResponseError"
import config from "../config"

ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
)

const Home = () => {
    const myDate = new Date();
    const [year, setYear] = useState(myDate.getFullYear())
    const [arrYear, seyArrYear] = useState(() => {
        let arr = []
        const currentYear = myDate.getFullYear();
        const startYear = (currentYear - 5)

        for (let i = startYear; i <= currentYear; i++) {
            arr.push(i);
        }

        return arr;
    })

    const [myData, setMyData] = useState([])
    const [option, setOption] = useState(() => {
        return { responsive: true, plugin: { legend: { position: 'top' } } }
    })

    useEffect(() => {
        fetchData()
    }, [])

    const fetchData = async () => {
        try {
            const payload = {
                year: year
            }
            const res = await axios.get(config.path + '/package/report-month', payload, config.headers())

        } catch (error) {
            ResponseError(error.message)
        }
    }

    return (
        <>
            <Template>
                <h1>Home</h1>
            </Template>
        </>
    )
}

export default Home
