import React, { useEffect, useState } from 'react'
import Template from '../components/Template'
import axios from 'axios'
import config from '../config'
import ResponseError from '../utils/ResponseError'
import Modal from '../components/Modal'
import Swal from 'sweetalert2'
import CloseModal from '../utils/CloseModal'

const Admin = () => {
    const [role, setRole] = useState(() => {
        return ['Admin', "Manager", "Staff"]
    })
    const [selectedRole, setSelectedRole] = useState('Admin')
    const [name, setName] = useState('')
    const [user, setUser] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [email, setEmail] = useState('')

    const [admins, setAdmins] = useState([])

    useEffect(() => {
        fetchData()
    }, [])

    const fetchData = async () => {
        try {
            const res = await axios.get(config.path + '/admin/list', config.headers())
            if (res.data.status) {
                setAdmins(res.data.data)
            }
        } catch (error) {
            ResponseError(error.message)
        }
    }

    const handleDelete = (item) => {
        Swal.fire({
            title: 'Delete',
            text: 'Are you sure to delete?',
            icon: 'question',
            confirmButtonColor: 'red',
            showCancelButton: true,
            showConfirmButton: true,
        }).then(async (e) => {
            try {
                if (e.isConfirmed) {
                    const res = await axios.delete(config.path + '/admin/delete/' + item.id, config.headers())
                    if (res.data.status) {
                        Swal.fire({
                            title: 'Deleted',
                            text: 'Deleted Success',
                            icon: 'success',
                            timer: 2000
                        })

                        fetchData()
                    }
                }
            } catch (error) {
                ResponseError(error.message)
            }
        })
    }

    const handleSave = async (e) => {
        e.preventDefault()

        if (password.trim() != confirmPassword.trim()) {
            ResponseError('รหัสผ่านไม่ตรงกัน')
            return;
        }

        try {
            const payload = {
                name,
                username: user,
                password,
                role: selectedRole,
                email,
            }
            const res = await axios.post(config.path + '/admin/create', payload, config.headers())
            if (res.data.status) {
                Swal.fire({
                    title: 'Created',
                    text: 'Created Success',
                    icon: 'success',
                    timer: 2000
                })

                CloseModal()

            }
        } catch (error) {
            ResponseError(error.message)
        }
    }

    return (
        <>
            <Template>
                <div className='card'>
                    <div className='card-header'>
                        <div className='card-title'>ผู้ใช้งาน</div>
                    </div>
                    <div className='card-body'>
                        <button data-bs-target="#adduser" data-bs-toggle="modal" className='btn btn-primary'>เพิ่มผู้ใช้</button>
                        <table className='table table-bordered table-striped mt-3'>
                            <thead>
                                <tr>
                                    <th>ชื่อ</th>
                                    <th>Username</th>
                                    <th>role</th>
                                    <th>email</th>
                                    <th></th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    admins.length > 0 ? admins.map((item, key) => (
                                        <tr>
                                            <td>{item.name}</td>
                                            <td>{item.username}</td>
                                            <td>{item.role}</td>
                                            <td>{item.email}</td>
                                            <td>
                                                <button className='btn btn-warning'>Edit</button>
                                                <button onClick={() => handleDelete(item)} className='btn btn-danger mx-2'>Delete</button>
                                            </td>

                                        </tr>
                                    )) : null
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
            </Template>
            <Modal title="เพิ่มผู้ใช้งาน" szie id="adduser">
                <form onSubmit={handleSave}>
                    <div>
                        <div className='mt-3'>
                            <label>ชื่อ</label>
                            <input onChange={(e) => setName(e.target.value)} type='text' className='form-control' required />
                        </div>
                        <div className='mt-3'>
                            <label>Username</label>
                            <input onChange={(e) => setUser(e.target.value)} type='text' className='form-control' required />
                        </div>
                        <div className='mt-3'>
                            <label>Password</label>
                            <input onChange={(e) => setPassword(e.target.value)} type='password' className='form-control' required />
                        </div>
                        <div className='mt-3'>
                            <label>Confirm Password</label>
                            <input onChange={(e) => setConfirmPassword(e.target.value)} type='password' className='form-control' required />
                        </div>
                        <div className='mt-3'>
                            <label>role</label>
                            <select className='form-control'>
                                {
                                    role.map((item) => <option value={selectedRole} onChange={(e) => setSelectedRole(e.target.value)}>{item}</option>)
                                }
                            </select>
                        </div>
                        <div className='mt-3'>
                            <label>email</label>
                            <input onChange={(e) => setEmail(e.target.value)} type='email' className='form-control' required />
                        </div>

                        <div className='mt-3'>
                            <button type='submit' className='btn btn-success'>บันทึก</button>
                        </div>
                    </div>
                </form>

            </Modal>
        </>
    )
}

export default Admin
