import { useState } from "react"
import Template from "../components/Template"

const ReportSumSalePerDay = () => {
    const [years, setYears] = useState(() => {
        let arr = []
        let d = new Date()
        let currentYear = d.getFullYear()
        let lastYear = currentYear - 5;

        for (let i = lastYear; i <= currentYear; i++) {
            arr.push(i)
        }
        return arr;
    })

    const [selectedYear, setSelectedYear] = useState(() => {
        return new Date().getFullYear()
    })

    return (
        <div>
            <Template>
                <div className="card">
                    <div className="card-header">
                        <div className="card-title">รายงานยอดขายรายวัน</div>
                    </div>
                    <div className="card-body">
                        <div className="row">
                            <div className="col-sm-2">
                                <div className="input-group">
                                    <span className="input-group-text">ปี</span>
                                    <select value={selectedYear} className="form-control">
                                        {
                                            years.map((item) => (
                                                <option></option>
                                            ))
                                        }
                                    </select>
                                </div>
                            </div>
                            <div className="col-sm-2">
                                <div className="input-group">
                                    <span className="input-group-text">เดือน</span>
                                    <select className="form-control"></select>
                                </div>
                            </div>
                            <div className="col-sm-8">
                                <button className="btn btn-primary">แสดงผล</button>
                            </div>
                        </div>

                    </div>

                </div>
            </Template>
        </div>
    )
}

export default ReportSumSalePerDay
