import React, { useEffect, useState } from 'react'
import Template from '../components/Template'
import axios from 'axios'
import config from '../config'
import ResponseError from '../utils/ResponseError'
import Modal from '../components/Modal'
import Swal from 'sweetalert2'
import CloseModal from '../utils/CloseModal'

const Admin = () => {
    // ... (ส่วน State และ Function เดิม) ...

    const [role, setRole] = useState(() => {
        return ['Admin', "Manager", "Staff"]
    })
    const [selectedRole, setSelectedRole] = useState('Admin')
    const [name, setName] = useState('')
    const [user, setUser] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [email, setEmail] = useState('')

    const [admins, setAdmins] = useState([])

    useEffect(() => {
        fetchData()
    }, [])

    const fetchData = async () => {
        try {
            // ... (Logic เดิม) ...
            const res = await axios.get(config.path + '/admin/list', config.headers())
            if (res.data.status) {
                setAdmins(res.data.data)
            }
        } catch (error) {
            ResponseError(error.message)
        }
    }

    const handleDelete = (item) => {
        // ... (Logic เดิม) ...
        Swal.fire({
            title: 'Delete',
            text: 'Are you sure to delete?',
            icon: 'question',
            confirmButtonColor: 'red',
            showCancelButton: true,
            showConfirmButton: true,
        }).then(async (e) => {
            try {
                if (e.isConfirmed) {
                    const res = await axios.delete(config.path + '/admin/delete/' + item.id, config.headers())
                    if (res.data.status) {
                        Swal.fire({
                            title: 'Deleted',
                            text: 'Deleted Success',
                            icon: 'success',
                            timer: 2000
                        })

                        fetchData()
                    }
                }
            } catch (error) {
                ResponseError(error.message)
            }
        })
    }

    const handleSave = async (e) => {
        e.preventDefault()

        if (password.trim() != confirmPassword.trim()) {
            ResponseError('รหัสผ่านไม่ตรงกัน')
            return;
        }

        try {
            const payload = {
                name,
                username: user,
                password,
                role: selectedRole,
                email,
            }
            const res = await axios.post(config.path + '/admin/create', payload, config.headers())
            if (res.data.status) {
                Swal.fire({
                    title: 'Created',
                    text: 'Created Success',
                    icon: 'success',
                    timer: 2000
                })

                CloseModal()
                // **ต้องเรียก fetchData() เพื่ออัพเดทตารางหลังบันทึกเสร็จ**
                fetchData()
                // **ควร clear state ของ form ด้วย**
                setName('')
                setUser('')
                setPassword('')
                setConfirmPassword('')
                setEmail('')

            }
        } catch (error) {
            ResponseError(error.message)
        }
    }

    // **ส่วนที่ปรับปรุง UI**
    return (
        <>
            <Template>
                <div className='card shadow-sm border-0'> {/* เพิ่ม shadow-sm และ border-0 ให้ดูทันสมัย */}
                    <div className='card-header bg-white d-flex justify-content-between align-items-center'>
                        <h4 className='mb-0 text-primary'>จัดการผู้ใช้งาน</h4> {/* ใช้ h4 และเน้นสี */}
                        <button
                            data-bs-target="#adduser"
                            data-bs-toggle="modal"
                            className='btn btn-primary d-flex align-items-center' // จัดปุ่มให้สวย
                        >
                            {/* ไอคอนสำหรับเพิ่มผู้ใช้ */}
                            <i className="bi bi-plus-lg me-2"></i>
                            เพิ่มผู้ใช้
                        </button>
                    </div>

                    <div className='card-body p-0'> {/* p-0 เพื่อให้ตารางชิดขอบการ์ดมากขึ้น */}
                        {/* ใช้ table-hover เพื่อให้มีไฮไลท์เวลาชี้ และ responsive-table สำหรับมือถือ */}
                        <div className='table-responsive'>
                            <table className='table table-striped table-hover align-middle mb-0'>
                                <thead className='table-light'> {/* เน้นส่วนหัวตาราง */}
                                    <tr>
                                        <th>ชื่อ</th>
                                        <th>Username</th>
                                        <th>Role</th>
                                        <th>Email</th>
                                        <th className='text-center'>Actions</th> {/* จัดหัวข้อให้อยู่ตรงกลาง */}
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        admins.length > 0 ? admins.map((item, key) => (
                                            <tr key={key}>
                                                <td>{item.name}</td>
                                                <td>{item.username}</td>
                                                <td><span className={`badge bg-${item.role === 'Admin' ? 'danger' : item.role === 'Manager' ? 'warning text-dark' : 'success'}`}>{item.role}</span></td> {/* ใช้ Badge เพื่อให้ Role ดูโดดเด่น */}
                                                <td>{item.email}</td>
                                                <td className='text-center'>
                                                    {/* ใช้ปุ่มขนาดเล็ก (btn-sm) และใช้ icon ในปุ่ม edit */}
                                                    <button className='btn btn-outline-warning btn-sm me-2'>
                                                        <i className="bi bi-pencil-square"></i> Edit
                                                    </button>
                                                    <button
                                                        onClick={() => handleDelete(item)}
                                                        className='btn btn-outline-danger btn-sm'
                                                    >
                                                        <i className="bi bi-trash"></i> Delete
                                                    </button>
                                                </td>
                                            </tr>
                                        )) : (
                                            <tr>
                                                <td colSpan="5" className='text-center text-muted p-4'>ไม่พบข้อมูลผู้ใช้งาน</td>
                                            </tr>
                                        )
                                    }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </Template>

            {/* Modal สำหรับเพิ่มผู้ใช้งาน */}
            <Modal title="เพิ่มผู้ใช้งานใหม่" szie id="adduser"> {/* ปรับ title ให้ชัดเจนขึ้น */}
                <form onSubmit={handleSave}>
                    <div className='row'> {/* ใช้ Grid System ของ Bootstrap ใน Modal เพื่อการจัดวางที่ดีขึ้น */}

                        {/* Name Field */}
                        <div className='col-md-6 mb-3'>
                            <label className='form-label fw-bold'>ชื่อ</label> {/* ใช้ form-label และ fw-bold */}
                            <input onChange={(e) => setName(e.target.value)} type='text' className='form-control' required />
                        </div>

                        {/* Username Field */}
                        <div className='col-md-6 mb-3'>
                            <label className='form-label fw-bold'>Username</label>
                            <input onChange={(e) => setUser(e.target.value)} type='text' className='form-control' required />
                        </div>

                        {/* Role Field */}
                        <div className='col-md-6 mb-3'>
                            <label className='form-label fw-bold'>Role</label>
                            {/* **แก้ไข: การจัดการ state ของ select ต้องทำที่ select เอง** */}
                            <select
                                className='form-select'
                                value={selectedRole}
                                onChange={(e) => setSelectedRole(e.target.value)}
                            >
                                {
                                    role.map((item, index) => (
                                        <option key={index} value={item}>
                                            {item}
                                        </option>
                                    ))
                                }
                            </select>
                        </div>

                        {/* Email Field */}
                        <div className='col-md-6 mb-3'>
                            <label className='form-label fw-bold'>Email</label>
                            <input onChange={(e) => setEmail(e.target.value)} type='email' className='form-control' required />
                        </div>

                        {/* Password Field */}
                        <div className='col-md-6 mb-3'>
                            <label className='form-label fw-bold'>Password</label>
                            <input onChange={(e) => setPassword(e.target.value)} type='password' className='form-control' required />
                        </div>

                        {/* Confirm Password Field */}
                        <div className='col-md-6 mb-3'>
                            <label className='form-label fw-bold'>Confirm Password</label>
                            <input onChange={(e) => setConfirmPassword(e.target.value)} type='password' className='form-control' required />
                        </div>

                        {/* Submit Button */}
                        <div className='col-12 mt-3 d-flex justify-content-end'> {/* จัดปุ่มไปทางขวา */}
                            <button type='submit' className='btn btn-success btn-lg'> {/* ใช้ btn-lg และไอคอน */}
                                <i className="bi bi-check-circle me-2"></i> บันทึก
                            </button>
                        </div>
                    </div>
                </form>

            </Modal>
        </>
    )
}

export default Admin
